import React, {PureComponent} from 'react'
import './style.css'

function isInteger(num) {
  //проверка на целочисленность
  return (num ^ 0) === num;
}


function isNumeric(n) {

   return !isNaN(parseFloat(n)) && isFinite(n);

   // Метод isNaN пытается преобразовать переданный параметр в число.
   // Если параметр не может быть преобразован, возвращает true, иначе возвращает false.
   // isNaN("12") // false
}

function roundPlus(x, n) { //x - число, n - количество знаков
  if(isNaN(x) || isNaN(n)) return false;
  var m = Math.pow(10,n);
  return Math.round(x*m)/m;
}

class Subscriber extends PureComponent {
  state = {
    isOpen: true,
    isEdit: false,
    isVoting: false,
  }
  handleClick = () => {
    this.setState({
      isOpen: !this.state.isOpen
    })
  }
  edit = () => {
    this.setState({
      isEdit: !this.state.isEdit
    })
  }
  voting = () => {
    this.setState({
      isVoting: !this.state.isVoting
    })
  }
  votingSave = () => {
    console.log(document.getElementById('selectAppearance').value);
    if (isNumeric(document.getElementById('appearance').value) && isNumeric(document.getElementById('intellect').value)
     && document.getElementById('intellect').value <= 10 && document.getElementById('appearance').value <= 10
     && document.getElementById('intellect').value >= 1 && document.getElementById('appearance').value >= 1
     && isInteger(Number(document.getElementById('intellect').value)) && isInteger(Number(document.getElementById('appearance').value))
   ) {
    this.props.subscriber.appearance = (Number(document.getElementById('appearance').value) + this.props.subscriber.appearance * this.props.subscriber.votes) / (this.props.subscriber.votes + 1);
    this.props.subscriber.intellect = (Number(document.getElementById('intellect').value) + this.props.subscriber.intellect * this.props.subscriber.votes) / (this.props.subscriber.votes + 1);
    this.props.subscriber.votes += 1;

    this.props.subscriber.appearance = roundPlus(this.props.subscriber.appearance, 2);
    this.props.subscriber.intellect = roundPlus(this.props.subscriber.intellect, 2);
    this.setState({
      isVoting: !this.state.isVoting,
      errorVoting: false
    })
  }
  }
  save = () => {
    this.props.subscriber.description = document.getElementById('newTxt').value;
    this.props.subscriber.img = document.getElementById('newImg').value;
    this.setState({
      isEdit: !this.state.isEdit
    })
  }
  render() {
    const {subscriber} = this.props;

    const voting = this.state.isVoting && this.state.isOpen && <div>
    <img src={subscriber.img} className="subscriber-image rounded-circle" alt="1"/>
    <div className="characteristic">
    <div class="form-group">
        <label for="exampleFormControlSelect1">Внешность</label>
        <select class="form-control" id="selectAppearance">
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option selected>5</option>
              <option>6</option>
              <option>7</option>
              <option>8</option>
              <option>9</option>
              <option>10</option>
            </select>
    </div>
    <div class="form-group">
        <label for="exampleFormControlSelect1">Интеллект</label>
        <select class="form-control" id="selectAppearance">
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
              <option selected>5</option>
              <option>6</option>
              <option>7</option>
              <option>8</option>
              <option>9</option>
              <option>10</option>
            </select>
    </div>
    </div>
    <button className="btn btn-info btn-lg" onClick={this.votingSave}>Оценить</button>
    <button className="btn btn-dark btn-lg float-right" onClick={this.voting}>Отмена</button>
    </div>

    const editing = this.state.isEdit && this.state.isOpen && <div>
    <div className="edditing-img">
    <img src={subscriber.img} className="subscriber-image rounded-circle" alt="1"/>
    <textarea rows="1" className="edding-img-img" defaultValue={subscriber.img} id="newImg"></textarea>
    </div>
    <textarea rows="3" className="form-control edding-textarea" defaultValue={subscriber.description} id="newTxt"></textarea>
    <button className="btn btn-info btn-lg edding-button" onClick={this.save}>Сохранить</button>
    </div>

    const description = !this.state.isEdit && this.state.isOpen && !this.state.isVoting && <div>
    <img src={subscriber.img} className="subscriber-image rounded-circle" alt="1"/>
    <div className="characteristic">
    <span>Внешность: <span className="mark">{subscriber.appearance}</span></span> <br />
    <span>Интеллект: <span className="mark">{subscriber.intellect}</span></span> <br />
    <span>Количество голосов: <span className="mark">{subscriber.votes}</span></span>
    </div>
    <section className="card-text description">{subscriber.description}</section>
    <button className="btn btn-danger btn-lg voting-button" onClick={this.voting}>Оценить</button>
    <button className="btn btn-warning btn-lg edding-button float-right" onClick={this.edit} style={{color: "white"}}>Изменить</button>
    </div>
    return (
      <div className="card mx-auto" style={{width: "50%"}}>
        <div className="card-header">
          <h2>
            {subscriber.name} {subscriber.surname}
            <button onClick={this.handleClick} className="btn btn-primary btn-lg float-right">
            {this.state.isOpen ? 'close' : 'open'}
            </button>
          </h2>
          <h6 className="card-subtitle text-muted" >
            Пол: {subscriber.gender}
          </h6>
        <div className="card-body">
          {voting}
          {editing}
          {description}
        </div>
        </div>
      </div>
    )
  }
}

export default Subscriber
