import React, {Component} from 'react'
import SubscriberList from '../SubscriberList/SubscriberList'
import subscribers from './subscribers'
import 'bootstrap/dist/css/bootstrap.css'
import './style.css'

class App extends Component {
  render() {
    return (
      <div className="container">
        <div className="jumbotron">
          <h1 className="display-3">ELARAY</h1>
          <h6 className="social-network">Social Network</h6>
        </div>
        <div className="form-group card mx-auto custom-form">
          <h4>Регистрация:</h4>
          <div className><label for="name">Имя</label><input id="name" type="text" placeholder="Имя" className="form-control" /></div>
          <div><label for="surname">Фамилия</label><input id="surname" type="text" placeholder="Фамилия" className="form-control" /></div>
          <div><label for="img">Ава</label><input id="img" type="text" placeholder="Укажите ссылку на аватарку" className="form-control"/></div>
          <div><label for="description">О себе</label><input id="description" type="text" placeholder="О себе" className="form-control"/></div>
          <button  id="accButton" type="button" name="button" className="btn btn-primary" onClick={this.creatingAcc}>Создать</button>
        </div>
        <SubscriberList subscribers={subscribers} />
      </div>
    )
  }
  creatingAcc = () => {
    subscribers.push(
      {
        "img": document.getElementById('img').value,
        "name": document.getElementById('name').value,
        "surname": document.getElementById('surname').value,
        "description": document.getElementById('description').value,
      },
      );
    console.log(subscribers[4]);
  }
}



export default App
