import React, {PureComponent} from 'react'

class Subscriber extends PureComponent {
  state = {
    isOpen: true,
  }
  handleClick = () => {
    this.setState({
      isOpen: !this.state.isOpen
    })
  }
  render() {
    const {subscriber} = this.props;
    const description = this.state.isOpen && <section className="card-text">{subscriber.description}</section>
    return (
      <div className="card mx-auto" style={{width: '55%'}}>
        <div className="card-header">
          <h2>
            {subscriber.name} {subscriber.surname}
            <button onClick={this.handleClick} className="btn btn-primary btn-lg float-right"></button>
          </h2>
        <div className="card-body">
          {description}
        </div>
        </div>
      </div>
    )
  }
}

export default Subscriber
