import React, {PureComponent} from 'react'
import './style.css'

class Subscriber extends PureComponent {
  state = {
    isOpen: true,
    isEdit: false,
  }
  handleClick = () => {
    this.setState({
      isOpen: !this.state.isOpen
    })
  }
  edit = () => {
    this.setState({
      isEdit: !this.state.isEdit
    })
  }
  save = () => {
    this.props.subscriber.appearance = (Number(document.getElementById('appearance').value) + this.props.subscriber.appearance * this.props.subscriber.votes) / (this.props.subscriber.votes + 1);
    this.props.subscriber.intellect = document.getElementById('intellect').value;
    this.props.subscriber.votes += 1;

    this.props.subscriber.description = document.getElementById('newTxt').value;
    this.props.subscriber.img = document.getElementById('newImg').value;
    this.setState({
      isEdit: !this.state.isEdit
    })
  }
  render() {
    const {subscriber} = this.props;

    const editing = this.state.isEdit && this.state.isOpen && <div>
    <div className="edditing-img">
    <img src={subscriber.img} className="subscriber-image rounded-circle" alt="1"/>
    <textarea rows="1" className="edding-img-img" defaultValue={subscriber.img} id="newImg"></textarea>
    </div>
    <div className="characteristic">
    <span>Внешность: <input type="number" className="votes" id="appearance" defaultValue={subscriber.appearance}></input></span> <br />
    <span>Интеллект: <input type="number" className="votes" id="intellect" defaultValue={subscriber.intellect}></input></span> <br />
    </div>
    <textarea rows="3" className="form-control edding-textarea" defaultValue={subscriber.description} id="newTxt"></textarea>
    <button className="btn btn-info btn-lg edding-button" onClick={this.save}>Сохранить</button>
    </div>

    const description = !this.state.isEdit && this.state.isOpen && <div>
    <img src={subscriber.img} className="subscriber-image rounded-circle" alt="1"/>
    <div className="characteristic">
    <span>Внешность: <span className="mark">{subscriber.appearance}</span></span> <br />
    <span>Интеллект: <span className="mark">{subscriber.intellect}</span></span> <br />
    <span>Количество голосов: <span className="mark">{subscriber.votes}</span></span>
    </div>
    <section className="card-text description">{subscriber.description}</section>
    <button className="btn btn-warning btn-lg edding-button" onClick={this.edit}>Изменить</button>
    </div>
    return (
      <div className="card mx-auto" style={{width: "50%"}}>
        <div className="card-header">
          <h2>
            {subscriber.name} {subscriber.surname}
            <button onClick={this.handleClick} className="btn btn-primary btn-lg float-right">
            {this.state.isOpen ? 'close' : 'open'}
            </button>
          </h2>
          <h6 className="card-subtitle text-muted" >
            Пол: {subscriber.gender}
          </h6>
        <div className="card-body">
          {editing}
          {description}
        </div>
        </div>
      </div>
    )
  }
}

export default Subscriber
